import { 
    REQUEST_LOGIN
} from '../actionTypes';

const initialState = {
    loading: false, 
    authUser: {},
}

export default function(state = initialState, action){
    switch(action.type){
        case REQUEST_LOGIN:
            return {...state, loading: true}
        case LOGIN: 
            return {
                ...state, 
                loading:false, 
                dataUser: action.payload
            }

        default: 
            return {...state}
    }
}
