import { put, call, takeEvery, takeLatest } from 'redux-saga';
import { 
    REQUEST_LOGIN
} from '../actionTypes';

function* requestLogin({payload}){
    console.log(payload);
}

function* watchRequestLogin(){
    yield takeLatest(REQUEST_LOGIN, requestLogin);
}

export default function* rootSaga(){
    yield all([
        watchRequestLogin(),
    ]);
}
